package assignment;

public class IsPrime {

	public static void main(String[] args) {
		int n=55;
	
		if(n<=1)
		System.out.println("not a prime");
		else
		{
			
			for(int i=2;i<n;i++)
			{
				if(n%2==0)
					System.out.println("not a prime");
					
			}
			
				System.out.println("prime");
		}
		}
	}

